
DROP TABLE IF EXISTS "coffee", "origin", "disponibility", "caracteristic";

CREATE TABLE IF NOT EXISTS "origin" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" TEXT NOT NULL,
  "created_at"  TIMESTAMPTZ NOT NULL DEFAULT CURRENT_TIMESTAMP,
  "updated_at"  TIMESTAMPTZ
);

CREATE TABLE IF NOT EXISTS "disponibility" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "dispo" TEXT NOT NULL,
  "created_at"  TIMESTAMPTZ NOT NULL DEFAULT CURRENT_TIMESTAMP,
  "updated_at"  TIMESTAMPTZ
);

CREATE TABLE IF NOT EXISTS "caracteristic" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "carac" TEXT NOT NULL,
  "created_at"  TIMESTAMPTZ NOT NULL DEFAULT CURRENT_TIMESTAMP,
  "updated_at"  TIMESTAMPTZ
);

CREATE TABLE IF NOT EXISTS "coffee" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" TEXT,
  "description" TEXT,
  "reference" INT,
  "origin_id" INTEGER,
  "price_kilo" REAL NOT NULL,
  "caracteristic_id" INTEGER,
  "disponibility_id" INTEGER,
  "created_at"  TIMESTAMPTZ NOT NULL DEFAULT CURRENT_TIMESTAMP,
  "updated_at"  TIMESTAMPTZ,

  FOREIGN KEY ("origin_id") REFERENCES "origin"("id") ON DELETE CASCADE,
  FOREIGN KEY ("caracteristic_id") REFERENCES "caracteristic"("id") ON DELETE CASCADE,
  FOREIGN KEY ("disponibility_id") REFERENCES "disponibility"("id") ON DELETE CASCADE
);

